import React from "react";
import useFetchMovieDetail from "../hooks/useFetchMovieDetail";

export default function MovieDetail() {
  const { loading, movie, error } = useFetchMovieDetail();

  return (
    <div>
      {error && console.log(error)};
      {loading ? (
        <h1>Loading...</h1>
      ) : (
        <div
          className={styles.movie_detail}
          style={{
            backgroundImage: `url(${movie.background_img})`,
          }}
        >
          <div className={styles.movie_card}>
            <h2 className={styles.title_text}>
              {movie.title}({movie.year})
            </h2>
            <div className={styles.movie_line}>
              <img
                className={styles.detail_img}
                src={movie.medium_cover_image}
                alt="영화 이미지"
              />
              <p>
                {movie.description_intro.length > 350
                  ? `${movie.description_intro.slice(0, 350)} ... `
                  : movie.description_intro}
              </p>
            </div>
            <ul>
              {movie.genres.map((g) => (
                <li key={g}>{g}</li>
              ))}
            </ul>
            <p>
              평점 : <span className={styles.rating}>{movie.rating}</span> / 10
            </p>
          </div>
        </div>
      )}
    </div>
  );
}
